mapper.update:
update project_task
text = #{text,jdbcType=VARCHAR}, start_date = #{start_date,jdbcType=TIMESTAMP}, end_date = #{end_date,jdbcType=TIMESTAMP}, real_start_date = #{real_start_date,jdbcType=TIMESTAMP}, real_end_date = #{real_end_date,jdbcType=TIMESTAMP}, parent = #{parent,jdbcType=INTEGER}, task_desc = #{taskDesc,jdbcType=VARCHAR}, priority = #{priority,jdbcType=INTEGER}, progress = #{progress,jdbcType=FLOAT}, state = #{state,jdbcType=INTEGER}, assign_staff_id = #{assignStaffId,jdbcType=INTEGER}, actor_staff_id = #{actorStaffId,jdbcType=INTEGER}, plan_workload = #{planWorkload,jdbcType=INTEGER}, real_workload = #{realWorkload,jdbcType=INTEGER}, is_delay = #{isDelay,jdbcType=INTEGER}, delay_reason = #{delayReason,jdbcType=VARCHAR}, readonly = #{readonly,jdbcType=INTEGER}, editable = #{editable,jdbcType=INTEGER}, type = #{type,jdbcType=VARCHAR}, project_id = #{projectId,jdbcType=INTEGER}, create_date = #{create_date,jdbcType=TIMESTAMP},
where id = #{id,jdbcType=INTEGER}

mapper.select:
select * 
from project_task
where parent !=0 and actor_staff_id = #{empId,jdbcType=INTEGER} OR assign_staff_id = #{empId,jdbcType=INTEGER}

select * 
from project_task
where parent !=0 and actor_staff_id = #{empId,jdbcType=INTEGER}

select * 
from project_task
where parent !=0 and assign_staff_id = #{empId,jdbcType=INTEGER}
